#include <stdio.h>

int factorial(int n) {
    if (n <= 1) {
        return 1;
    }
    return n * factorial(n - 1);
}

int main() {
    int num = 5;
    int result = factorial(num);

    printf("Factorial of %d is %d\n", num, result);

    for (int i = 0; i <= 5; i++) {
        printf("factorial(%d) = %d\n", i, factorial(i));
    }

    return 0;
}

#include <stdio.h>

int fibonacci(int n) {
    if (n <= 1) {
        return n;
    }
    return fibonacci(n - 1) + fibonacci(n - 2);
}

int main() {
    int terms = 10;

    printf("Fibonacci sequence up to %d terms:\n", terms);

    for (int i = 0; i < terms; i++) {
        printf("%d ", fibonacci(i));
    }
    printf("\n");

    return 0;
}

#include <stdio.h>

#define SIZE 5

int main() {
    int arr[SIZE] = {10, 20, 30, 40, 50};
    int sum = 0;
    int max = arr[0];
    int min = arr[0];

    // Calculate sum, max, and min
    for (int i = 0; i < SIZE; i++) {
        sum += arr[i];

        if (arr[i] > max) {
            max = arr[i];
        }

        if (arr[i] < min) {
            min = arr[i];
        }
    }

    float average = (float)sum / SIZE;

    printf("Array elements: ");
    for (int i = 0; i < SIZE; i++) {
        printf("%d ", arr[i]);
    }
    printf("\n");

    printf("Sum: %d\n", sum);
    printf("Average: %.2f\n", average);
    printf("Max: %d\n", max);
    printf("Min: %d\n", min);

    return 0;
}

#include <stdio.h>

int main() {
    int x = 42;
    int *ptr = &x;
    int **pptr = &ptr;

    printf("Value of x: %d\n", x);
    printf("Address of x: %p\n", (void*)&x);
    printf("Value of ptr (address of x): %p\n", (void*)ptr);
    printf("Value pointed to by ptr: %d\n", *ptr);
    printf("Value of pptr (address of ptr): %p\n", (void*)pptr);
    printf("Value pointed to by pptr: %p\n", (void*)*pptr);
    printf("Value pointed to by *pptr: %d\n", **pptr);

    // Modify value through pointer
    *ptr = 100;
    printf("\nAfter modification:\n");
    printf("Value of x: %d\n", x);
    printf("Value pointed to by ptr: %d\n", *ptr);

    // Array and pointer arithmetic
    int arr[] = {1, 2, 3, 4, 5};
    int *arr_ptr = arr;

    printf("\nArray elements via pointer:\n");
    for (int i = 0; i < 5; i++) {
        printf("arr[%d] = %d\n", i, *(arr_ptr + i));
    }

    return 0;
}

#include <stdio.h>
#include <string.h>

typedef struct {
    char name[50];
    int age;
    float gpa;
} Student;

void print_student(Student s) {
    printf("Name: %s\n", s.name);
    printf("Age: %d\n", s.age);
    printf("GPA: %.2f\n", s.gpa);
    printf("\n");
}

int main() {
    Student students[3];

    // Initialize students
    strcpy(students[0].name, "Alice");
    students[0].age = 20;
    students[0].gpa = 3.8;

    strcpy(students[1].name, "Bob");
    students[1].age = 21;
    students[1].gpa = 3.5;

    strcpy(students[2].name, "Charlie");
    students[2].age = 19;
    students[2].gpa = 3.9;

    // Print all students
    printf("Student Records:\n\n");
    for (int i = 0; i < 3; i++) {
        print_student(students[i]);
    }

    // Find student with highest GPA
    int highest_index = 0;
    for (int i = 1; i < 3; i++) {
        if (students[i].gpa > students[highest_index].gpa) {
            highest_index = i;
        }
    }

    printf("Student with highest GPA:\n");
    print_student(students[highest_index]);

    return 0;
}

#include <stdio.h>
#include <stdlib.h>

int main() {
    FILE *file;
    char filename[] = "data.txt";

    // Write to file
    file = fopen(filename, "w");
    if (file == NULL) {
        printf("Error opening file for writing!\n");
        return 1;
    }

    fprintf(file, "Hello, File I/O!\n");
    fprintf(file, "This is a test file.\n");
    fprintf(file, "Numbers: %d %d %d\n", 10, 20, 30);

    fclose(file);
    printf("Data written to file successfully.\n\n");

    // Read from file
    file = fopen(filename, "r");
    if (file == NULL) {
        printf("Error opening file for reading!\n");
        return 1;
    }

    printf("Reading from file:\n");
    printf("==================\n");

    char line[100];
    while (fgets(line, sizeof(line), file) != NULL) {
        printf("%s", line);
    }

    fclose(file);

    return 0;
}

#include <stdio.h>

void print_pattern(int n, int level) {
    if (n <= 0) return;

    // Print spaces for indentation
    for (int i = 0; i < level; i++) {
        printf("  ");
    }

    printf("Level %d: Processing %d\n", level, n);

    // Recursive calls
    print_pattern(n - 1, level + 1);
    print_pattern(n - 2, level + 1);
}

int main() {
    int depth = 4;
    printf("Recursive tree pattern (depth = %d):\n\n", depth);
    print_pattern(depth, 0);

    return 0;
}
